import React, { Component } from "react";
import {Alert} from "reactstrap"
import {connect} from "react-redux"
import {BASE_URL} from "../assets/Asset"
import axios from "axios";
import {Login_action} from "../reducers/Action"
import "./index.css";


const MapStateToProps = (state)=>({
  user : state
})
const MapDispatchToProps =(dispatch) =>({
  Login_action : (user)=>{
   dispatch(Login_action(user));
  }
})

class Login extends Component {
  constructor(props) {
    super(props);
    this.onchangeEmail = this.onchangeEmail.bind(this);
    this.onchangePassword = this.onchangePassword.bind(this);
    this.handlesubmit = this.handlesubmit.bind(this);

    this.state = {
      user: [],
      email: "",
      password: "",
      isLogin: false,
      error : false,
      message : "",
      loading : false
    };
  }

  onchangeEmail(event) {
    this.setState({email: event.target.value,});
  }
  onchangePassword(event) {
    this.setState({ password: event.target.value,});
  }

  handlesubmit = () => {
    const { email, password} = this.state
    if (email.length < 5 && password.length < 3){
      this.setState({error : true, message : "Please Enter A Valid Data"},()=>{
          window.setTimeout(()=>{
          this.setState({
          error : false
          })
        }, 3000)
      })
    } else {
      
      const postdata = { email: email.toLowerCase(), password: password, };
    const prev = this.props.location.state ||  "/"
      this.setState({ loading: true }) 
    axios.post( BASE_URL + "login/", postdata)
        .then((resp) => {
          if (resp.status === 200){
            this.props.Login_action(resp.data);
            this.setState({user : resp.data})

            this.setState({ loading: false }) 
            this.props.history.push(prev)
            console.log(prev)
        }
        else if ( resp.status === 300){
        this.setState({error: true, loading: false, message: "InValid Data"}, () => {
            window.setTimeout(() => {
            this.setState({error: false})
          }, 3000)
        })} 
      })
      .catch( (err)=> {
        this.setState({ error: true, loading: false, message: "Error While Trying To  Login"}, 
        () => {
          window.setTimeout(() => {
          this.setState({error: false, loading:false})
        },3000)})
      });
    }
  };
  handleReg = ()=>{
    this.props.history.push("/reg")
  }
  handleLogin = () => {
   
    this.props.history.push("/login")
  }

  render() {
    return (
      <div className="">
        <div className="container reg-login">
          <div className="row">
            <div className="col">
              <button className="btn-pay">
                <span onClick={this.handleReg} className="btn-pay reg-sm text-left">
                  Register
                </span>
                <span
                  onClick={this.handleLogin}
                  className="btn-pay login-sm text-right "
                >
                  Login
                </span>
              </button>
            </div>
          </div>
        </div>
        <div className="login-form">
          <Alert isOpen={this.state.error} color="danger">
            {this.state.message}{" "}
          </Alert>
          <div className="container ">
            <div className="row">
              <div className="col">
                <input
                  placeholder="E-mail"
                  className="text-center"
                  type="text"
                  onChange={this.onchangeEmail}
                  value={this.state.email}
                />
              </div>
            </div>
          </div>
          <div className="container">
            <div className="row">
              <div className="col">
                <input
                  placeholder="Password"
                  type="password"
                  autoComplete="on"
                  className="text-center"
                  onChange={this.onchangePassword}
                  value={this.state.password}
                />
              </div>
            </div>
          </div>
          <div className="container">
            <div className="row">
              <div className="col">
                <button
                  type="submit"
                  className=" btn-pay"
                  onClick={this.handlesubmit}
                  loading={this.state.loading && <i className="fa fa-spinner"/>}
                >
                  Login
                </button>
              </div>
            </div>
          </div>
          <div className="container">
            <div className="row">
              <div className="col">
                <p className="registered">
                  Not registered?{" "}
                  <span onClick={this.handleReg}> Register </span>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default connect( MapStateToProps, MapDispatchToProps)(Login)
